
/*
 * tagname dictionary?
 * urls for plcs?
 * port numbers, etc.
 * */
var _ = require('underscore.min');

var tagnames = {'well_running':{'plc':'wellhouse','point':'di0'},
                'open_gate':{'plc':'wellhouse','point':'do13'}},
    plcs = {'wellhouse':{'ip':'172.17.17.50','port':2020}},
	points = {};

_.each(tagnames,function(v,k,list){
		if(points.hasOwnProperty(v.plc)){
			points[v.plc].push({point: v.point, tagname: k,value:null,lastUpdate:null});
		}
		else{
			points[v.plc] = [];
			points[v.plc].push({point: v.point, tagname: k,value:null,lastUpdate:null});
		}
});
exports.tagnames = tagnames;
exports.plcs = plcs;
exports.points = points;

